styles:
.sidebar {
    height: 100%;
    color:white;
    background-color: #ebeced;
    overflow: hidden;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-start;
}

.sidebar-menu {
    flex: 0 0 auto;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: column;
    align-content: flex-start;
    align-items: flex-start;
    justify-content: flex-start;
}

.sidebar-menu.expanded .sidebar-button .sidebar-button-label {
    display: inline-block
}

.sidebar-button {
    height: 36px;
    width: 100%;
    cursor: pointer;
    display: inline-block;
    white-space: nowrap;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: row;
    align-content: center;
    align-items: center;
    justify-content: flex-start;
    color:#333;
    font-size: 12px;
    user-select: none;
}

.sidebar-button:hover, .sidebar-button:focus, .sidebar-button.active {
    background: #caccce;
    text-decoration: none;
}

.sidebar-button:hover {
    background: #dfe1e2;
}

.sidebar-button:hover, .sidebar-button:focus {
    text-decoration: underline;
}

.sidebar-button.sideloaded{
    color: #f60;
    font-weight: bold;
}

.sidebar-button-label{
    display: none;
    flex: 1 1 auto;
    font-size: 13px;
    margin-right: 15px;
}

.sidebar-button-icon {
    flex: 0 0 auto;
    height: 36px;
    width: 36px;
    background-size: 18px;
    background-position: center;
    background-repeat: no-repeat;
    display: inline-block;
    font-size: 14px;
    text-align: center;
    line-height: 36px;
    vertical-align: middle;
}

