styles:
.growl {
  z-index: 200;
  position: fixed;
  width: 300px;
  right: 0px;
  top: 0px;
}

.growl-item {
  margin-top: 12px;
  margin-right: 12px;
}

/* Theme styles */
.alert-error-theme {
  background-color: #D82128;
  color: #FFFFFF;
}

.alert-info-theme {
  background-color: #F2F2F2;
  color: #231F20;
}

.alert-warning-theme {
  background-color: #FFC20A;
  color: #231F20;
}

/* Link styles */

.alert-link-list {
  padding: 1px 1px 1px 15px;
}

.alert-error-theme a {
  color: #FFFFFF;
}

a {
  text-decoration: underline;
}

.alert-link {
  cursor: pointer;
  padding-right: 10px;
}

/* Icon styles */

.alert-icon {
  height: 48px;
  width: 48px;
  background: transparent;
  border: 0;
}

.icon-win-info::before {
  font-size: 24px;
  padding-left: 12px;
  padding-top: 12px;
}

.icon-win-warning::before {
  font-size: 24px;
  padding-left: 12px;
  padding-top: 12px;
}

.icon-win-error::before {
  font-size: 24px;
  padding-left: 12px;
  padding-top: 12px;
}

.icon-win-cancel::before {
  font-size: 15px;
}

/* Content styles */

.alert-content {
  padding-top: 12px;
  padding-bottom: 12px;
  overflow: hidden;
  word-wrap: break-word;
}

h1 {
  padding: 0 0 8px 0;
  font-size: 18px;
  line-height: 24px;
  font-family: "Segoe UI","Helvetica Neue",Helvetica,Arial,sans-serif;
}

p {
  font-size: 13px;
  padding: 0;
  line-height: normal;
  font-family: "Segoe UI","Helvetica Neue",Helvetica,Arial,sans-serif;
  vertical-align: middle;
  margin-top: 2px;
}

